import { useState } from 'react';
import './OuterBox.css'

function OuterBox(props) {
    const[initialText, setText]= useState('');

    const inputChangeHandler = (event)=>{
        setText(initialText, event.target.value);
        // console.log(event.target.value);
    }

    const submitHandler = (event)=>{
        event.preventDefault();

        const taskNew = {
            title: initialText
        }

        props.onSaveTask(taskNew);
        setText('');
    }

    return (
        <div className='bigBox'>
            <div className='outerBox'>
                <p className='courseGoal'>Course Goal</p>
                <input type='text' onChange={inputChangeHandler}></input>
                <br />
                <button className='addGoal' type='submit' onSubmit={submitHandler}>Add Goal</button>
            </div>
        </div>
    );
};

export default OuterBox;
